import request from "@/utils/request";

const basePath = "/menu";

export default {
    get() {
        return request.get(basePath).then(res => {
            if (res.data.code === 0) return res.data.data;
        })
    },
    create(menu) {
        return request.post(basePath, menu);
    },
    modify(menu) {
        return request.put(basePath, menu);
    },
    delete() {
    }
}
