import {RequestUtil} from '@/utils';

const basePath = "/menu";

export default {
    get() {
        return RequestUtil.get(basePath).then(res => {
            if (res.data.code === 0) return res.data.data;
        })
    },
    create(menu) {
        return RequestUtil.post(basePath, menu);
    },
    modify(menu) {
        return RequestUtil.put(basePath, menu);
    },
    delete() {
    }
}
