let formItemLayout = {
    labelCol: {span: 6},
    wrapperCol: {span: 16},
};

export default {
    data() {
        return {
            form: this.$form.createForm(this),
            formItemLayout,
            loginDecorator: {
                username: [
                    'username',
                    {
                        rules: [
                            {required: true, message: '账号不能为空'}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                password: [
                    'password',
                    {
                        rules: [
                            {required: true, message: '密码不能为空'},
                            {min: 3, max: 12, message: '密码长度不能小于3位，大于16位'}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                userMail: [
                    'userMail',
                    {
                        rules: [
                            {type: 'email', message: '不合法邮箱'},
                            {required: true, message: '邮箱不能为空'}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                userPhone: [
                    'userPhone',
                    {
                        rules: [
                            {required: true, message: '手机号不能为空'}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                confirm: [
                    'confirm',
                    {
                        rules: [
                            {required: true, message: '确认密码不能为空'},
                            {validator: this.bindBlurConfirmPassword}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                remember: [
                    'remember',
                    {
                        valuePropName: 'checked',
                        initialValue: true,
                    }
                ],
                agreement: [
                    'agreement',
                    {
                        valuePropName: 'checked',
                        initialValue: true,
                        rules: [
                            {required: true, message: '请先阅读隐私规范'}
                        ]
                    }
                ],
                roleName: [
                    'roleName',
                    {
                        rules: [
                            {required: true, message: '角色名称不能为空'},
                            {validator: this.bindBlurCheckRoleName}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                roleDescription: [
                    'roleDescription'
                ],
            },
            registerDecorator: {
                username: [
                    'username',
                    {
                        rules: [
                            {required: true, message: '账号不能为空'}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
            },
            roleDecorator: {
                roleName: [
                    'roleName',
                    {
                        rules: [
                            {required: true, message: '角色名称不能为空'},
                            {validator: this.bindBlurCheckRoleName}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                roleDescription: [
                    'roleDescription'
                ],
                roleStatus: [
                    'roleStatus',
                    {
                        valuePropName: 'checked',
                        initialValue: true
                    }
                ]
            },
            userDecorator: {
                userName: [
                    'userName',
                    {
                        rules: [
                            {required: true, message: '账号不能为空'}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                userPass: [
                    'userPass',
                    {
                        rules: [
                            {required: true, message: '密码不能为空'},
                            {min: 3, max: 12, message: '密码长度不能小于3位，大于16位'}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                userMail: [
                    'userMail',
                    {
                        rules: [
                            {type: 'email', message: '不合法邮箱'},
                            {required: true, message: '邮箱不能为空'}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                userPhone: [
                    'userPhone',
                    {
                        rules: [
                            {required: true, message: '手机号不能为空'}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                confirm: [
                    'confirm',
                    {
                        rules: [
                            {required: true, message: '确认密码不能为空'},
                            {validator: this.bindBlurConfirmPassword}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                roleId: [
                    'roleId',
                    {
                        rules: [
                            {required: true, message: '请选择角色'},
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                roleName: [
                    'roleName',
                    {
                        rules: [
                            {required: true, message: '角色名称不能为空'},
                            {validator: this.bindBlurCheckRoleName}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                roleDescription: [
                    'roleDescription'
                ],
            },
            menuDecorator: {
                menuName: [
                    'menuName',
                    {
                        rules: [
                            {required: true, message: '菜单名称不能为空'}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                menuPath: [
                    'menuPath',
                    {
                        rules: [
                            {required: true, message: '菜单路径不能为空'}
                        ],
                        validateTrigger: ['blur']
                    }
                ],
                menuComponent: [
