                    //     ],
                    //     validateTrigger: ['blur']
                    // }
                ],
                menuParentId: [
                    'menuParentId'
                ],
                menuIcon: [
                    'menuIcon'
                ]
            }
        }
    },
    methods: {
        bindBlurCheckRoleName(rule, value, callback) {
            this.$request.get(`/role/${value}`).then(res => res.data.data !== null ? callback("角色已存在") : callback());
        },
        bindBlurConfirmPassword(rule, value, callback) {
            const form = this.form;
            if (value && value !== form.getFieldValue("userPass")) callback('两次密码不一致!');
            else callback();
        },
    }
}
