import Request from '@/utils/request';

const basePath = "/user";

export default {
    get(pagination) {
        return Request.get(basePath, pagination).then(res => {
            if (res.data.code === 0) return res.data.data;
        })
    },
    create(user) {
        return Request.post(basePath, user).then(res => {
            if (res.data.code === 0) return res.data.data;
        })
    },
    modify(user) {
        return Request.put(basePath, user).then(res => {
            if (res.data.code === 0) return res.data.data;
        })
    },
    delete(ids) {
        return Request.delete(`${basePath}/${ids}`).then(res => {
            if (res.data.code === 0) return res.data.data;
        })
    }
}
