import storage from "@/utils/storage";

let user = storage.get('user') || {
    username: 'user',
    password: '123',
    avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png'
};

export default {
    namespaced: true,
    state: {
        user
    },
    mutations: {
        setUser(state, user) {
            storage.set('user', user);
            state.user = user;
        }
    },
    actions: {}
}
