function getPermissions(vnode) {
    return vnode.context.$store.state.account.permissions;
}

const hasPermission = {
    install(Vue) {
        Vue.directive('hasPermission', {
            bind(el, binding, vnode) {
                let permissions = get;
                let values = binding.value.split(',');
                let flag = true;
                for (let value of values) {
                    if (!permissions.includes(value)) flag = false;
                }

                if (!flag) {
                    if (!el.parentNode) el.style.display = "none";
                    else el.parentNode.removeChild(el);
                }
            }
        });
    }
};
const hasNoPermission = {
    install(Vue) {
        Vue.directive('hasPermission', {
            bind(el, binding, vnode) {
                let permissions = vnode.context.$store.state.account.permissions;
                let values = binding.value.split(',');
                let flag = true;
                for (let value of values) {
                    if (!permissions.includes(value)) flag = false;
                }

                if (!flag) {
                    if (!el.parentNode) el.style.display = "none";
                    else el.parentNode.removeChild(el);
                }
            }
        });
    }
};

export default [hasPermission, hasNoPermission];
