import {RequestUtil} from '@/utils';

const basePath = "/role";

export default {
    get(pagination) {
        return RequestUtil.get(basePath, pagination).then(res => {
            if (res.data.code === 0) return res.data.data;
        })
    },
    create(role) {
        return RequestUtil.post(basePath, role);
    },
    modify(role) {
        return RequestUtil.put(basePath, role);
    },
    delete(ids) {
        return RequestUtil.delete(`${basePath}/${ids}`);
    }
}
