import LayoutView from '@/layouts/LayoutView.vue';
import CommonView from '@/views/Common.vue';

export default [
    {
        path: '/',
        name: '首页',
        component: LayoutView,
        redirect: '/workbench',
        children: [
            {
                path: '/workbench',
                name: '工作台',
                component: () => import('@/views/page/Workbench.vue')
            },
            {
                path: '/dashboard',
                name: '仪表盘',
                redirect: '/dashboard/analysis',
                component: CommonView,
                children: [
                    {
                        path: '/dashboard/analysis',
                        name: '数据分析',
                        component: () => import('@/views/page/dashboard/analysis/Analysis.vue')
                    },
                    {
                        path: '/dashboard/monitor',
                        name: '系统监控',
                        component: () => import('@/views/page/dashboard/monitor/Monitor.vue')
                    },
                ]
            },
            {
                path: '/system',
                name: '系统管理',
                redirect: '/system/user',
                component: CommonView,
                children: [
                    {
                        path: '/system/user',
                        name: '用户管理',
                        component: () => import('@/views/page/system/user/User.vue')
                    },
                    {
                        path: '/system/role',
                        name: '角色管理',
                        component: () => import('@/views/page/system/role/Role.vue')
                    },
                    {
                        path: '/system/permission',
                        name: '权限管理',
                        component: () => import('@/views/page/system/permission/Permission.vue')
                    },
                    {
                        path: '/system/menu',
                        name: '菜单管理',
                        component: () => import('@/views/page/system/menu/Menu.vue')
                    },
                ]
            },
            {
                path: '/account',
                name: '个人中心',
                component: CommonView,
                redirect: '/account/profile',
                children: [
                    {
                        path: '/account/profile',
                        name: '个人详情',
                        component: () => import('@/views/page/account/profile/Profile.vue'),
                        redirect: '/account/profile/notification',
                        children: [
                            {
                                path: '/account/profile/notification',
                                name: '通知管理',
                                component: () => import('@/views/page/account/profile/notification/Notification.vue'),
                            },
                            {
                                path: '/account/profile/message',
                                name: '消息中心',
                                component: () => import('@/views/page/account/profile/message/Message.vue'),
                            },
                            {
                                path: '/account/profile/todo',
                                name: '待办事项',
                                component: () => import('@/views/page/account/profile/todo/Todo.vue'),
                            },
                            {
                                path: '/account/profile/logs',
                                name: '操作日志',
                                component: () => import('@/views/page/account/profile/logs/Logs.vue'),
                            },
                        ]
                    },
                    {
                        path: '/account/setting',
                        name: '账户设置',
                        component: () => import('@/views/page/account/setting/Setting.vue'),
                        redirect: '/account/setting/basic',
                        children: [
                            {
                                path: '/account/setting/basic',
                                name: '基本设置',
                                component: () => import('@/views/page/account/setting/basic/SettingBasic.vue'),
                            },
                            {
                                path: '/account/setting/security',
                                name: '安全设置',
                                component: () => import('@/views/page/account/setting/security/SettingSecurity.vue'),
                            },
                            {
                                path: '/account/setting/binding',
                                name: '账号绑定',
                                component: () => import('@/views/page/account/setting/binding/SettingBinding.vue'),
                            },
                            {
                                path: '/account/setting/notification',
                                name: '新消息通知',
                                component: () => import('@/views/page/account/setting/notification/SettingNotification.vue'),
                            }
                        ]
                    },
                ]
            },
        ]
    },
]
