import {RequestUtil} from '@/utils';

const basePath = "/user";

export default {
    get(pagination) {
        return RequestUtil.get(basePath, pagination).then(res => {
            if (res.data.code === 0) return res.data.data;
        })
    },
    create(user) {
        return RequestUtil.post(basePath, user).then(res => {
            if (res.data.code === 0) return res.data.data;
        })
    },
    modify(user) {
        return RequestUtil.put(basePath, user).then(res => {
            if (res.data.code === 0) return res.data.data;
        })
    },
    delete(ids) {
        return RequestUtil.delete(`${basePath}/${ids}`).then(res => {
            if (res.data.code === 0) return res.data.data;
        })
    }
}
