.i-layout {
    > .ant-layout {
        min-height: 100vh;
    }

    > .ant-layout-header,
    > .ant-layout-content {
        max-width: 1200px;
        padding-left: 24px;
        padding-right: 24px;
        margin: 0 auto;
    }

    > .ant-layout-content {
        padding-top: 24px;
        padding-bottom: 24px;
    }
}

.i-layout-fluid {
    > .ant-layout-header,
    > .ant-layout-content {
        max-width: 100%;
        margin: 0 auto;
    }
}
