import PageView from "@/layouts/PageView";

export default {
    name: "Setting",
    components: {PageView},
    data() {
        return {
            selectedKeys: []
        }
    },
    mounted() {
        this.selectedKeys = [this.$route.path];
    },
    methods: {
        bindClickMenu(e) {
            const {key} = e;
            this.$router.push(key);
            this.selectedKeys = [key];
        }
    },
    watch: {
        '$route'(value) {
            this.selectedKeys = [value.path];
        }
    }
}
