let treeDataSource = [];
let checkedIds = [];
let defaultCheckedKeys = [];

export default {
    name: "RoleConfig",
    props: {
        value: {
            type: Boolean,
            default: false
        },
        role: {
            type: Object,
            default() {
                return {}
            }
        }
    },
    data() {
        return {
            checkedIds,
            treeDataSource,
            defaultCheckedKeys,
        }
    },
    methods: {
        apiFetchMenu() {
            return this.$request.get('/menu').then(res => {
                if (res.data.code === 0) this.treeDataSource = res.data.data.rows.children;
                return res;
            })
        },
        apiModifyRole(role) {
            return this.$request.put('/role', role);
        },
        fetchMenuList() {
            this.apiFetchMenu();
        },
        bindClickSubmit() {
            this.role.menuId = this.checkedIds.join(',');

            this.apiModifyRole(this.role).then(() => {
                this.bindClickCancel();
                this.$emit("success");
            });
        },
        bindClickCancel() {
            this.defaultCheckedKeys = [];
            this.checkedIds = [];
            this.$emit("input", false);
        },
        bindClickCheck(e) {
            this.checkedIds = e.checked;
        }
    },
    watch: {
        role() {
            this.fetchMenuList();
        }
    }
}
